
jQuery(function(){jQuery.fn.autoClear=function(){if($(this).is("input")){jQuery(this).each(function(){jQuery(this).data("autoclear",jQuery(this).attr("value"));});jQuery(this).bind('focus',function(){if(jQuery(this).attr("value")==jQuery(this).data("autoclear")){jQuery(this).attr("value","").addClass('autoclear-normalcolor');}}).bind('blur',function(){if(jQuery(this).attr("value")==""){jQuery(this).attr("value",jQuery(this).data("autoclear")).removeClass('autoclear-normalcolor');}});return jQuery(this);}
if($(this).is("textarea")){jQuery(this).each(function(){jQuery(this).data("autoclear",jQuery(this).text());});jQuery(this).bind('focus',function(){if(jQuery(this).text()==jQuery(this).data("autoclear")){jQuery(this).text("").addClass('autoclear-normalcolor');}}).bind('blur',function(){if(jQuery(this).text()==""){jQuery(this).text(jQuery(this).data("autoclear")).removeClass('autoclear-normalcolor');}});return jQuery(this);}}});